mapper.insert:
insert
  into send_mail
     ( mail_no
     , mem_no
     , mail_writer
     , mail_receiver
     , mail_title
     , mail_cnt
     )
	   values 
	 ( seq_mailno.nextval
	 , #{memNo}
	 , #{mailWriter}
	 , #{mailReceiver}
	 , #{mailTitle}
	 , #{mailContent}
	 )

insert
  into mail_file
     ( mail_file_no
     , mail_no
     , mail_origin
     , mail_update
     , mail_file_path
     , mail_filesize
     , mail_filetype
   	 )
   	   values
   	 ( seq_mailfno.nextval
   	 , seq_mailno.currval
   	 , #{mailOrigin}
   	 , #{mailUpdate}
   	 , #{mailFPath}
   	 , #{mailFSize}
   	 , #{mailFType}
   	 )

insert
  into send_mail
     ( mail_no
     , mem_no
     , mail_writer
     , mail_receiver
     , mail_title
     , mail_cnt
     , temp_box
     )
	   values 
	 ( seq_mailno.nextval
	 , #{memNo}
	 , #{mailWriter}
	 , #{mailReceiver}
	 , #{mailTitle}
	 , #{mailContent}
	 , 'Y'
	 )

insert
  into mail_file
     ( mail_file_no
     , mail_no
     , mail_origin
     , mail_update
     , mail_file_path
     , mail_filesize
     , mail_filetype
   	 )
   	   values
   	 ( seq_mailfno.nextval
   	 , seq_mailno.currval
   	 , #{mailOrigin}
   	 , #{mailUpdate}
   	 , #{mailFPath}
   	 , #{mailFSize}
   	 , #{mailFType}
   	 )

mapper.update:
update receive_mail
   set 
       recycle_bin_status = 'A'
 where mail_no = #{mailNo}
   and mail_receiver = #{email}

update receive_mail
   set
       receive_status = 'Y'
 where mail_no = #{mailNo}
   and mail_receiver = #{mailReceiver}
