mapper.insert:
insert 
  into menu_board
	 ( 
	   weekmenu_no
	 , mem_no
	 , weekmenu_title
	 , weekmenu_cnt 
	 )
	 values
			( 
			  seq_menuno.nextval
			, #{memNo}
			, #{weekmenuTitle}
			, #{weekmenuCnt} 
			)

mapper.update:
update 
	   menu_board
   set 
   	   weekmenu_title = #{weekmenuTitle}
	 , weekmenu_cnt = #{weekmenuCnt}
	 , weekmenu_updatedate = sysdate
 where 
 	   weekmenu_no = #{weekmenuNo}
