mapper.insert:
insert 
  into product
     (
       pdt_no
     , pdt_ctg
     , pdt_file
     , pdt_name
     , pdt_detail
     , org_price
     , mem_price
     , pdt_stock
     , pdt_enrolldate
     )
values
     (
       SEQ_PRNO.NEXTVAL
     , #{ pdtCtg }
     , #{ pdtFile }
     , #{ pdtName }
     , #{ pdtDetail }
     , #{ orgPrice }
     , #{ memPrice }
     , #{ pdtStock }
     , SYSDATE
     )

mapper.update:
   update
	      product
	  set pdt_file = #{pdtFile}
	    , pdt_ctg = #{pdtCtg}
	    , pdt_name = #{pdtName}
	    , org_price = #{orgPrice}
        , mem_price = #{memPrice}
        , pdt_stock = #{pdtStock}
	    , pdt_status = #{pdtStatus}
	    , pdt_detail = #{pdtDetail}
	where pdt_no = #{pdtNo}

mapper.delete:
delete 
  from product
 where pdt_no = #{ pdtNo }

delete from cart
where cart_no = #{cartNo}
and mem_no = #{memNo}
